mapper.select:
select COUNT(*) from GOM_BDW_SRV_ORD_INFO_VIEW
where TRADE_ID = #{tradeId}
 and ACTIVE_TYPE != '101' 
 and ORDER_TYPE = #{orderType}

select COUNT(*) from GOM_BDW_SRV_ORD_INFO_VIEW
where SERIAL_NUMBER = #{serialNumber}
 and ACTIVE_TYPE != '101' 
 and ORDER_TYPE = #{orderType}

select TO_CHAR(wm_concat(co.APPLY_ORD_ID)) from GOM_BDW_CST_ORD co left join gom_BDW_srv_ord_info soi on co.CST_ORD_ID=soi.CST_ORD_ID where soi.ACTIVE_TYPE='102' and soi.SRV_ORD_STAT='10F' and soi.INSTANCE_ID in (select INSTANCE_ID from gom_BDW_srv_ord_info where SRV_ORD_ID in
#{srvOrdId}
)
