mapper.sql:
select tab.* from ( SELECT DISTINCT a.id as AFFAIR_DISPATCH_ORDER_ID, a.order_id as ORDER_ID, a.code as AFFAIR_DISPATCH_ORDER_CODE, a.title as TITLE, a.content as CONTENT, a.dispose_staff_arr as DISPOSE_STAFF_ARR, a.create_staff as CREATE_STAFF_ID, a.FILE_INFO as FILE_INFO, f.user_name as CREATE_STAFF_NAME, g.user_name as CHECK_STAFF_NAME, to_char(a.create_date,'yyyy-MM-dd hh24:mi:ss') as CREATE_DATE, a.is_check as IS_CHECK, a.state as STATE, a.type as TYPE, a.check_staff as CHECK_STAFF, e.tache_name as TACHE_NAME, c.wo_state as WO_STATE, c.wo_id as WO_ID, c.wo_code as WO_CODE, to_char(c.REQ_FIN_DATE,'yyyy-MM-dd hh24:mi:ss') as REQ_FIN_DATE, c.priv_forward_wo_id as PRIV_FORWARD_WO_ID,
CASE WHEN c.REQ_FIN_DATE <= sysdate then '超时单' WHEN c.ALARM_DATE <= sysdate and sysdate < c.REQ_FIN_DATE then '预警单' WHEN c.ALARM_DATE > sysdate then '正常单' ELSE '正常单' END as WO_COMPLETE_STATE,
row_number() over (partition by a.order_id order by c.wo_id desc) rn from GOM_BDW_AFFAIR_DISPATCH_ORDER a left join GOM_ORDER b on b.order_id = a.order_id LEFT JOIN GOM_WO c ON c.ORDER_ID = b.ORDER_ID LEFT JOIN GOM_PS_2_WO_S d ON d.ID = c.PS_ID LEFT JOIN UOS_TACHE e on e.ID = d.TACHE_ID LEFT join vw_pub_staff f on f.staff_id = a.create_staff LEFT join vw_pub_staff g on g.staff_id = a.check_staff where 1=1
and a.create_staff= #{staffId}
and c.DISP_OBJ_ID = #{staffId}
and e.tache_code= #{tacheCode}
and a.state= #{orderState}
and a.type= #{orderType}
and a.code like '%' || #{affOrderCode} || '%'
and a.title like '%' || #{orderTitle} || '%'
and a.create_date >= to_date( #{beginDate},'yyyy-mm-dd')
and a.create_date <= to_date( #{endDate},'yyyy-mm-dd')
and a.state != '290000112'
and c.priv_forward_wo_id is not null
) tab where tab.rn = 1 order by tab.AFFAIR_DISPATCH_ORDER_ID desc

mapper.select:
        select z.*
        FROM (
        SELECT ROW_NUMBER() OVER (ORDER BY y.CREATE_DATE desc) AS rowindex, y.*
        From (

        ) y
        )z
        where 1=1

	        and z.rowindex >#{startRow,jdbcType=INTEGER} and z.rowindex <=#{endRow,jdbcType=INTEGER}

mapper.sql:
SELECT DISTINCT a.id as AFFAIR_DISPATCH_ORDER_ID, a.order_id as ORDER_ID, a.code as AFFAIR_DISPATCH_ORDER_CODE, a.title as TITLE, a.content as CONTENT, a.dispose_staff_arr as DISPOSE_STAFF_ARR, a.create_staff as CREATE_STAFF_ID, a.FILE_INFO as FILE_INFO, f.user_name as CREATE_STAFF_NAME, g.user_name as CHECK_STAFF_NAME, to_char(a.create_date,'yyyy-MM-dd hh24:mi:ss') as CREATE_DATE, a.is_check as IS_CHECK, a.state as STATE, a.type as TYPE, a.check_staff as CHECK_STAFF, e.tache_name as TACHE_NAME, c.wo_state as WO_STATE, c.wo_id as WO_ID, c.wo_code as WO_CODE, to_char(c.REQ_FIN_DATE,'yyyy-MM-dd hh24:mi:ss') as REQ_FIN_DATE, c.priv_forward_wo_id as PRIV_FORWARD_WO_ID,
CASE WHEN c.REQ_FIN_DATE <= sysdate then '超时单' WHEN c.ALARM_DATE <= sysdate and sysdate < c.REQ_FIN_DATE then '预警单' WHEN c.ALARM_DATE > sysdate then '正常单' ELSE '正常单' END as WO_COMPLETE_STATE
from GOM_BDW_AFFAIR_DISPATCH_ORDER a left join GOM_ORDER b on b.order_id = a.order_id LEFT JOIN GOM_WO c ON c.ORDER_ID = b.ORDER_ID LEFT JOIN GOM_PS_2_WO_S d ON d.ID = c.PS_ID LEFT JOIN UOS_TACHE e on e.ID = d.TACHE_ID LEFT join vw_pub_staff f on f.staff_id = a.create_staff LEFT join vw_pub_staff g on g.staff_id = a.check_staff where 1=1
and a.create_staff= #{staffId}
and e.tache_code= #{tacheCode}
and a.state= #{orderState}
and a.type= #{orderType}
and a.code like '%' || #{affOrderCode} || '%'
and a.title like '%' || #{orderTitle} || '%'
and a.create_date >= to_date( #{beginDate},'yyyy-mm-dd')
and a.create_date <= to_date( #{endDate},'yyyy-mm-dd')
order by a.id desc

