SELECT t2.REQ_FIN_DATE, t2.APPLY_ORD_ID,t2.APPLY_ORD_NAME, t2.tache_id,t2.tache_name,t2.CUST_NAME_CHINESE,wm_concat(t2.staffLoginName) as staffLoginName from ( select DISTINCT MAX(t.REQ_FIN_DATE) AS REQ_FIN_DATE, t.APPLY_ORD_ID,t.APPLY_ORD_NAME, t.tache_id,t.tache_name,t.CUST_NAME_CHINESE,t.staffLoginName from ( SELECT w.DISP_OBJ_TYE,w.comp_user_id, to_char(w.REQ_FIN_DATE, 'yyyy-MM-dd') AS REQ_FIN_DATE, cst.APPLY_ORD_ID,cst.APPLY_ORD_NAME, ws.tache_id,ut.tache_name,w.ORDER_ID,w.WO_ID,cst.CUST_NAME_CHINESE,s.login_name as staffLoginName FROM GOM_WO w LEFT JOIN GOM_ORDER o ON o.ORDER_ID = w.ORDER_ID LEFT JOIN gom_bdw_sec_local_relate_info rel ON rel.order_id = o.ORDER_ID LEFT JOIN GOM_BDW_SRV_ORD_INFO srv ON srv.order_id = rel.parent_ORDER_ID LEFT JOIN gom_bdw_cst_ord cst ON srv.cst_ord_id = cst.cst_ord_id LEFT JOIN GOM_PS_2_WO_S ws ON ws.id = w.ps_id LEFT JOIN UOS_TACHE ut ON ut.id = ws.tache_id left join vw_pub_staff s on s.staff_id = w.comp_user_id where w.disp_obj_id != '-2000' and w.WO_STATE = '290000005' --and w.WO_STATE in ('290000004','290000005') AND ut.tache_code = #{tacheCode} and w.ORDER_ID IN
#{orderId}
ORDER BY w.create_date DESC ) t GROUP BY t.APPLY_ORD_ID,t.APPLY_ORD_NAME,t.tache_id,t.tache_name,t.CUST_NAME_CHINESE,t.staffLoginName )t2 GROUP BY t2.REQ_FIN_DATE, t2.APPLY_ORD_ID,t2.APPLY_ORD_NAME,t2.tache_id,t2.tache_name,t2.CUST_NAME_CHINESE

SELECT * FROM gom_bdw_send_msg_log r WHERE r.apply_ord_id = #{applyOrdId} AND r.apply_ord_name = #{applyOrdName}
 AND r.cust_name_chinese = #{custNameChinese} AND r.sms_content = #{smsContent}
 AND r.user_name = #{userName} AND r.tache_id = #{tacheId} AND r.oper_action = #{operAction}

SELECT rel.order_id FROM gom_bdw_sec_local_relate_info rel WHERE rel.parent_order_id = (
   SELECT rel.parent_order_id FROM gom_bdw_sec_local_relate_info rel WHERE rel.order_id = #{orderId}
)

SELECT * FROM ( SELECT wo.wo_state,wo.ORDER_ID FROM gom_order oo LEFT JOIN gom_wo wo ON wo.order_id = oo.order_id LEFT JOIN GOM_PS_2_WO_S ws ON ws.id = wo.ps_id LEFT JOIN UOS_TACHE ut ON ut.id = ws.tache_id WHERE oo.order_id IN
#{orderId}
AND ut.tache_code = 'CROSS_WHOLE_COURDER_TEST' ORDER BY wo.create_date DESC ) WHERE ROWNUM = 1
