select info.SRV_ORD_ID as srvOrdId, info.ORDER_ID as orderId, info.CST_ORD_ID as cstOrdID, info.ORDER_TYPE as itemType, c.CUST_NAME_CHINESE as custName, info.SERIAL_NUMBER as serialNumber, info.TRADE_ID as tradeId, o.ORDER_CODE as orderCode, c.APPLY_ORD_ID as applyOrdId, c.APPLY_ORD_NAME as applyOrdName, attr.attr_value as dianlNo, pc.code_value as prodBustType, pc.code_content as prodBustTypeName, pt.code_value as actCode, pt.code_content as actCodeName, '' as actTypeName, o.ORDER_STATE as actTypeState, vs.user_name as dispObjName, case when info.CREATE_DATE is not null then to_char(info.CREATE_DATE, 'yyyy-MM-dd hh24:mi:ss') else '' end as createDateStr, o.PARENT_ORDER_ID as omlParentOrderId, o.OLD_ORDER_ID as omlOrderId from GOM_BDW_CST_ORD c inner join GOM_BDW_SRV_ORD_INFO info on info.cst_ord_id = c.cst_ord_id inner join (select to_char(f.staff_id) as staff_id, f.user_name, f.login_name, f.department from vw_pub_staff f) vs on vs.staff_id = info.user_id left join gom_order o on info.order_id = o.order_id left join gom_BDW_srv_ord_attr_info attr on info.SRV_ORD_ID = attr.SRV_ORD_ID and attr.ATTR_CODE = '20000064' left join ( select code_content,code_value from gom_BDW_code_info where CODE_type = 'product_code' ) pc on info.SERVICE_ID = pc.code_value left join ( select code_content,code_value from gom_BDW_code_info where CODE_type = 'operate_type' ) pt on info.ACTIVE_TYPE = pt.code_value where 1=1 and info.SYSTEM_RESOURCE = 'flow-schedule-lt'
and info.RESOURCES = #{isLocalUnicom}
and info.SRV_ORD_STAT = #{orderState}
and info.SRV_ORD_STAT <> #{wholeOrderState}
and c.APPLY_ORD_ID like '%'||#{applyOrdId}||'%'
and c.APPLY_ORD_NAME like '%'||#{applyOrdName}||'%'
and c.CUST_NAME_CHINESE like '%'||#{custName}||'%'
and info.SERVICE_ID = #{productType}
and info.ACTIVE_TYPE = #{actType}
and vs.staff_id = #{operStaffId}
and info.ORDER_TYPE = #{orderType}
order by info.CREATE_DATE desc

select
from GOM_BDW_CST_ORD c inner join GOM_BDW_SRV_ORD_INFO info on info.cst_ord_id = c.cst_ord_id join (select count(srv_ord_id) as num,cst_ord_id from GOM_BDW_SRV_ORD_INFO where SRV_ORD_STAT = '10F' group by cst_ord_id) srv on srv.cst_ord_id = c.cst_ord_id inner join (select to_char(f.staff_id) as staff_id, f.user_name, f.login_name, f.department from vw_pub_staff f) vs on vs.staff_id = info.user_id inner join gom_order o on info.order_id = o.order_id left join ( select code_content,code_value from gom_BDW_code_info where CODE_type = 'product_code' ) pc on info.SERVICE_ID = pc.code_value left join ( select code_content,code_value from gom_BDW_code_info where CODE_type = 'operate_type' ) pt on info.ACTIVE_TYPE = pt.code_value RIGHT JOIN ( --一个省份所有的员工 SELECT u.USER_ID AS userId FROM GOM_USER_ORG_VIEW u WHERE u.AREA_ID = ( SELECT u.AREA_ID FROM gom_user_s u LEFT JOIN gom_org_s o ON o.org_id = u.ORG_ID LEFT JOIN gom_area_s s ON s.id = u.area_id LEFT JOIN gom_bdw_code_info info ON info.code_value = s.id AND info.code_type = 'if_designation_user_or_job' WHERE 1=1
and u.user_id = #{operStaffId}
) ) qz on qz.userId = info.user_id where 1=1 and info.SYSTEM_RESOURCE = 'flow-schedule-lt'
and info.RESOURCES = #{isLocalUnicom}
and info.SRV_ORD_STAT = #{orderState}
and info.SRV_ORD_STAT <> #{wholeOrderState}
and c.APPLY_ORD_ID like '%'||#{applyOrdId}||'%'
and c.APPLY_ORD_NAME like '%'||#{applyOrdName}||'%'
and c.CUST_NAME_CHINESE like '%'||#{custName}||'%'
and info.SERVICE_ID = #{productType}
and info.ACTIVE_TYPE = #{actType}
and info.ORDER_TYPE = #{orderType}

