mapper.sql:
select info.SRV_ORD_ID as srvOrdId, info.srv_ord_ids as srvordIds, info.circodeCount as circodeCount, info.ORDER_ID as orderId, info.CST_ORD_ID as cstOrdID, info.ORDER_TYPE as itemType, c.CUST_NAME_CHINESE as custName, '' as serialNumber, '' as tradeId, '' as orderCode, c.APPLY_ORD_ID as applyOrdId, c.APPLY_ORD_NAME as applyOrdName, attr.ATTR_VALUE as dianlNo, pc.code_value as prodBustType, pc.code_content as prodBustTypeName, pt.code_value as actCode, pt.code_content as actCodeName, '' as actTypeName, o.ORDER_STATE as actTypeState, vs.user_name as dispObjName, case when info.CREATE_DATE is not null then to_char(info.CREATE_DATE, 'yyyy-MM-dd hh24:mi:ss') else '' end as createDateStr, o.PARENT_ORDER_ID as omlParentOrderId, o.OLD_ORDER_ID as omlOrderId from GOM_BDW_CST_ORD c inner join (select f.cst_ord_id,f.srv_ord_id,f.srv_ord_ids,f.circodeCount,r.ORDER_ID,r.ORDER_TYPE,r.CREATE_DATE,r.SERVICE_ID,r.user_id,r.ACTIVE_TYPE from GOM_BDW_SRV_ORD_INFO r inner join (select cst_ord_id,max(srv_ord_id) as srv_ord_id,WM_CONCAT(srv_ord_id) as srv_ord_ids,count(*) as circodeCount from GOM_BDW_SRV_ORD_INFO where 1=1
and RESOURCES = #{isLocalUnicom}
and SRV_ORD_STAT = #{orderState}
and SRV_ORD_STAT <> #{wholeOrderState}
and SERVICE_ID = #{productType}
and ACTIVE_TYPE = #{actType}
and ORDER_TYPE = #{orderType}
group by cst_ord_id) f on r.cst_ord_id = f.cst_ord_id and r.srv_ord_id = f.srv_ord_id where r.SYSTEM_RESOURCE = 'flow-schedule-lt' order by r.CREATE_DATE desc) info on info.cst_ord_id = c.cst_ord_id inner join (select to_char(f.staff_id) as staff_id, f.user_name, f.login_name, f.department from vw_pub_staff f) vs on vs.staff_id = info.user_id left join (select SRV_ORD_ID,ATTR_VALUE FROM GOM_BDW_SRV_ORD_ATTR_INFO where attr_code = '20000064' group by SRV_ORD_ID,ATTR_VALUE) attr on info.SRV_ORD_ID = attr.SRV_ORD_ID left join gom_order o on info.order_id = o.order_id left join ( select code_content,code_value from gom_BDW_code_info where CODE_type = 'product_code' ) pc on info.SERVICE_ID = pc.code_value left join ( select code_content,code_value from gom_BDW_code_info where CODE_type = 'operate_type' ) pt on info.ACTIVE_TYPE = pt.code_value where 1=1
and c.APPLY_ORD_ID like '%'||#{applyOrdId}||'%'
and c.APPLY_ORD_NAME like '%'||#{applyOrdName}||'%'
and c.CUST_NAME_CHINESE like '%'||#{custName}||'%'
and vs.staff_id = #{operStaffId}

select info.SRV_ORD_ID as srvOrdId, info.ORDER_ID as orderId, info.CST_ORD_ID as cstOrdID, info.ORDER_TYPE as itemType, c.CUST_NAME_CHINESE as custName, info.SERIAL_NUMBER as serialNumber, info.TRADE_ID as tradeId, '' as orderCode, c.APPLY_ORD_ID as applyOrdId, c.APPLY_ORD_NAME as applyOrdName, '' as dianlNo, pc.code_value as prodBustType, pc.code_content as prodBustTypeName, pt.code_value as actCode, pt.code_content as actCodeName, '' as actTypeName, '' as actTypeState, vs.user_name as dispObjName, case when info.CREATE_DATE is not null then to_char(info.CREATE_DATE, 'yyyy-MM-dd hh24:mi:ss') else '' end as createDateStr, '' as omlParentOrderId, '' as omlOrderId from GOM_BDW_CST_ORD c inner join (select * from (select f.*,row_number() over(partition by CST_ORD_ID order by SRV_ORD_ID asc) rn from GOM_BDW_SRV_ORD_INFO f where 1=1
and f.RESOURCES = #{isLocalUnicom}
and f.SRV_ORD_STAT = #{orderState}
and f.SERVICE_ID = #{productType}
and f.ACTIVE_TYPE = #{actType}
and f.ORDER_TYPE = #{orderType}
order by f.CREATE_DATE desc ) where rn = 1) info on info.cst_ord_id = c.cst_ord_id inner join (select to_char(f.staff_id) as staff_id, f.user_name, f.login_name, f.department from vw_pub_staff f) vs on vs.staff_id = info.user_id left join ( select code_content,code_value from gom_BDW_code_info where CODE_type = 'product_code' ) pc on info.SERVICE_ID = pc.code_value left join ( select code_content,code_value from gom_BDW_code_info where CODE_type = 'operate_type' ) pt on info.ACTIVE_TYPE = pt.code_value where 1=1 and info.SYSTEM_RESOURCE = 'flow-schedule-lt'
and c.APPLY_ORD_ID like '%'||#{applyOrdId}||'%'
and c.APPLY_ORD_NAME like '%'||#{applyOrdName}||'%'
and c.CUST_NAME_CHINESE like '%'||#{custName}||'%'
and vs.staff_id = #{operStaffId}

