) a WHERE a.WO_STATE = '290000002' AND a.DEAL_USER_ID IS NULL AND a.DISP_OBJ_TYE = '260000001' AND a.DISP_OBJ_ID IN (SELECT ORG_ID FROM GOM_USER_S WHERE USER_ID = #{staffId} ) union all /*岗位待办*/ SELECT b.id, b.circuit_code, b.serial_number, b.service_id, b.code_content, b.instance_id, b.region_id, b.order_id, b.wo_id, b.tacheId, b.tache_name, b.pub_date_name, b.system_resource, b.is_oldres, b.WO_STATE, b.DEAL_USER_ID, b.disp_obj_id, b.DISP_OBJ_TYE, b.create_date FROM (
) b WHERE b.WO_STATE = '290000002' AND b.DEAL_USER_ID IS NULL AND b.DISP_OBJ_TYE = '260000002' AND b.DISP_OBJ_ID IN (SELECT STAFF_GROUP_ID FROM GOM_STAFF_GROUP_RELA_S WHERE STAFF_ID = #{staffId} ) union all /*个人待办*/ SELECT c.id, c.circuit_code, c.serial_number, c.service_id, c.code_content, c.instance_id, c.region_id, c.order_id, c.wo_id, c.tacheId, c.tache_name, c.pub_date_name, c.system_resource, c.is_oldres, c.WO_STATE, c.DEAL_USER_ID, c.disp_obj_id, c.DISP_OBJ_TYE, c.create_date FROM (
) c WHERE c.WO_STATE = '290000002' AND c.DEAL_USER_ID IS NULL AND c.DISP_OBJ_TYE = '260000003' AND c.DISP_OBJ_ID = #{staffId}

