insert into GOM_BDW_SRV_ORD_INFO (SRV_ORD_ID, CST_ORD_ID, ORDER_ID, SERVICE_ID, TRADE_TYPE_CODE, ACTIVE_TYPE, SERVICE_OFFER_ID, SERIAL_NUMBER, TRADE_ID,
