#{TRADE_ID_RELA},#{USER_ID},#{FLOW_ID},sysdate, #{RESOURCES}, #{SYSTEM_RESOURCE}, #{RE_ACTIVE_TYPE}, #{INSTANCE_ID}, #{ORDER_TYPE}, #{CHANGE_FLAG}, #{BUSS_SON_TYPE}
, #{REQ_FIN_TIME}
, #{PARALLEL_FLAG}
