SELECT /*+ ALL_ROWS */ nvl(to_char(SERVICETYPE), '总计') "TYPE", sum(case when operate_value = '101' then 1 else 0 end) "ADD", sum(case when operate_value = '102' then 1 else 0 end) "DEL", sum(case when operate_value = '103' then 1 else 0 end) "CHA", sum(case when operate_value = '104' then 1 else 0 end) "STOP", sum(case when operate_value = '105' then 1 else 0 end) "REP", sum(case when operate_value = '106' then 1 else 0 end) "MOVE", count(operate_value) "COUNT" from ( select CI.CODE_CONTENT as SERVICETYPE, aa.active_type as operate_value from GOM_BDW_SRV_ORD_INFO aa left join GOM_BDW_CST_ORD bb on aa.CST_ORD_ID = bb.CST_ORD_ID left join gom_bdw_code_info ci on aa.service_id = ci.CODE_VALUE WHERE aa.SYSTEM_RESOURCE = 'second-schedule-lt' AND ci.CODE_TYPE = 'product_code' and bb.HANDLE_DEP_ID in ( SELECT B.ORG_ID FROM GOM_ORG_S B START WITH B.ORG_ID = (SELECT ORG_ID FROM GOM_ORG_S A WHERE A.PARENT_ORG_ID = 1 START WITH A.ORG_ID = (SELECT ORG_ID FROM GOM_USER_S WHERE USER_ID = #{userId}) CONNECT BY PRIOR A.PARENT_ORG_ID = A.ORG_ID) CONNECT BY B.PARENT_ORG_ID = PRIOR B.ORG_ID )
/*截止日期*/ AND bb.CREATE_DATE <= to_date(#{endDate},'yyyy-mm-dd')
/*开始日期*/ AND bb.CREATE_DATE >= to_date(#{beginDate},'yyyy-mm-dd')
) group by rollup(SERVICETYPE)

