select /*+parallel (6)*/ SERIAL_NUMBER, SUBSCRIBE_ID, APPLY_ORD_ID, to_date(CREATE_DATE,'yyyy-mm-dd hh24:mi:ss')CREATE_DATE, APPLY_ORD_NAME, DISPATCH_ORDER_ID, SERIAL_NUMBER, SERVICETYPE, OPERTYPE, TACHE_NAME, CIRCUITCODE, to_date(REPORTTIME,'yyyy-mm-dd hh24:mi:ss') REPORTTIME, case when ISSUPPLEMENT ='1' then '是' else '否' end as ISSUPPLEMENT, REQ_FIN_DATE, A_INSTALLED_ADD, Z_INSTALLED_ADD, AREGIONNAME, ZREGIONNAME, A_CITY, Z_CITY, A_REQ_FIN_DATE, Z_REQ_FIN_DATE, ORG_NAME, USER_REAL_NAME, OPERTYPE, DISPATCH_ORDER_NO, SERVICETYPE FROM(SELECT ROW_NUMBER() OVER (ORDER BY SRV.SRV_ORD_ID desc) AS rowindex, SRV.*, UT.TACHE_NAME, ATTR.CIRCUITCODE AS CIRCUITCODE, ATTR.REPORTTIME, case when RES.Order_Id is not null then '1' else '0' end as ISSUPPLEMENT, case when attr.oqc is not null then oqc when attr.lqc is not null then lqc when attr.jqc is not null then jqc end as REQ_FIN_DATE, ATTR.A_installed_add, ATTR.Z_installed_add, ATTR.AregionName AREGIONNAME, ATTR.ZregionName ZREGIONNAME, ATTR.a_city, ATTR.z_city, ATTR.A_REQ_FIN_DATE, ATTR.Z_REQ_FIN_DATE, US.ORG_NAME, US.USER_REAL_NAME, CI01.CODE_CONTENT AS OPERTYPE, DI.DISPATCH_ORDER_NO, CI.CODE_CONTENT AS SERVICETYPE FROM ( SELECT * FROM ( SELECT AA.*, ROWNUM AS RN FROM ( select SOI.SRV_ORD_ID, /**客户订单号*/ CO.SUBSCRIBE_ID, /**申请单号*/ CO.APPLY_ORD_ID, /**申请单创建时间*/ to_char(CO.CREATE_DATE, 'yyyy-mm-dd hh24:mi:ss') CREATE_DATE, /**申请单标题*/ CO.APPLY_ORD_NAME, /**产品类型（编码）*/ SOI.SERVICE_ID, /**动作类型(编码)*/ SOI.ACTIVE_TYPE, SOI.DISPATCH_ORDER_ID, SOI.ORDER_ID, SOI.SERIAL_NUMBER from GOM_BDW_SRV_ORD_INFO SOI JOIN GOM_BDW_CST_ORD CO ON SOI.CST_ORD_ID = CO.CST_ORD_ID WHERE SOI.SYSTEM_RESOURCE = 'second-schedule-lt'
AND SOI.SERVICE_ID = #{productCode}
AND SOI.ACTIVE_TYPE = #{operateType}
/*截止日期*/ AND CO.CREATE_DATE <= to_date(#{endDate},'yyyy-mm-dd')
/*开始日期*/ AND CO.CREATE_DATE >= to_date(#{beginDate},'yyyy-mm-dd')
AND CO.HANDLE_DEP_ID IN (SELECT B.ORG_ID FROM GOM_ORG_S B START WITH B.ORG_ID = (SELECT ORG_ID FROM GOM_ORG_S A WHERE A.PARENT_ORG_ID = 1 START WITH A.ORG_ID = (SELECT ORG_ID FROM GOM_USER_S WHERE USER_ID = #{userId}) CONNECT BY PRIOR A.PARENT_ORG_ID = A.ORG_ID) CONNECT BY B.PARENT_ORG_ID = PRIOR B.ORG_ID) order by SOI.SRV_ORD_ID desc )AA )TT ) SRV LEFT JOIN GOM_BDW_RES_SUPPLEMENT_INFO RES ON SRV.SERIAL_NUMBER = RES.SERIAL_NUMBER LEFT JOIN REPORT_SRV_ORD_ATTR_INFO ATTR ON ATTR.SRV_ORD_ID = SRV.SRV_ORD_ID LEFT JOIN GOM_BDW_DISPATCH_ORDER DI ON DI.DISPATCH_ORDER_ID = SRV.DISPATCH_ORDER_ID LEFT JOIN GOM_BDW_CODE_INFO CI ON CI.CODE_VALUE = SRV.SERVICE_ID LEFT JOIN GOM_BDW_CODE_INFO CI01 ON CI01.CODE_VALUE = SRV.ACTIVE_TYPE LEFT JOIN GOM_ORDER O ON SRV.ORDER_ID = O.ORDER_ID LEFT JOIN (select * from( select W.WO_ID, w.order_id, w.disp_obj_id, w.DEAL_USER_ID, w.disp_obj_tye, W.REQ_FIN_DATE, w.PS_ID, row_number() OVER(PARTITION BY order_id ORDER BY wo_id DESC) rn from gom_wo w) where rn =1) WO ON WO.ORDER_ID = O.ORDER_ID LEFT JOIN GOM_PS_2_WO_S PS ON WO.PS_ID = PS.ID LEFT JOIN UOS_TACHE UT ON PS.TACHE_ID = UT.ID LEFT JOIN GOM_USER_S US ON WO.DEAL_USER_ID = US.USER_ID WHERE CI.CODE_TYPE = 'product_code' AND CI01.CODE_TYPE = 'operate_type'
and RES.Order_Id is not null
and RES.Order_Id is null
) WHERE rowindex > #{startRow}
and rowindex <= #{endRow}

