where aa.SYSTEM_RESOURCE = 'second-schedule-lt'
AND aa.SERVICE_ID = #{productCode}
AND res.order_id is not null
and RES.Order_Id is null
and aa.ACTIVE_TYPE = #{operateType}
and bb.HANDLE_DEP_ID in ( SELECT B.ORG_ID FROM GOM_ORG_S B START WITH B.ORG_ID = (SELECT ORG_ID FROM GOM_ORG_S A WHERE A.PARENT_ORG_ID = 1 START WITH A.ORG_ID = (SELECT ORG_ID FROM GOM_USER_S WHERE USER_ID = #{userId}) CONNECT BY PRIOR A.PARENT_ORG_ID = A.ORG_ID) CONNECT BY B.PARENT_ORG_ID = PRIOR B.ORG_ID )
/*截止日期*/ AND bb.CREATE_DATE <= to_date(#{endDate},'yyyy-mm-dd')
/*开始日期*/ AND bb.CREATE_DATE >= to_date(#{beginDate},'yyyy-mm-dd')

